import React, { useEffect, useState } from 'react';
import { View, Image, Text} from 'react-native';
import { useNavigation } from "@react-navigation/native";

import {RectButton} from 'react-native-gesture-handler';

import styles from './styles';

import landingImg from '../../assets/images/landing.png';
import studyIcon  from '../../assets/images/icons/study.png';
import giveClassesIcon  from '../../assets/images/icons/give-classes.png';
import heartIcon  from '../../assets/images/icons/heart.png';
import api from '../../services/api';



function Landing(){

    const {navigate} = useNavigation();

    const [totalConnections, setTotalConnections] = useState(0);

    useEffect(()=>{
        api.get('/connections').then(res => {
            const { total } = res.data;

            setTotalConnections(total);
        });
    },[]);

    function handleNavigationToGiveClassesPage(){
        navigate('GiveClasses');
    }

    function handleNavigateToStudyPages(){
        navigate('Study');
    }

    return(
        <View style={styles.container}>
           <Image source={landingImg} style={styles.bunner} />

           <Text style={styles.title}>
                Seja bem-vindo, {'\n'}
                <Text style={styles.titleBold}>O que deseja Fazer?</Text>
           </Text>

           <View style={styles.buttonsContainer}>

                <RectButton onPress={handleNavigateToStudyPages} style={[styles.button, styles.buttonPrimary]}>
                    <Image source={studyIcon} />

                    <Text style={styles.buttonText}>Estudar</Text>
                </RectButton>

                <RectButton 
                    onPress={handleNavigationToGiveClassesPage} 
                    style={[styles.button, styles.buttonSecondary]}
                >
                    <Image source={giveClassesIcon} />

                    <Text style={styles.buttonText}>Dar Aulas</Text>
                </RectButton>

           </View>

           <Text style={styles.totalConnectios}>
               Total de {totalConnections} conexções já realizadas {' '}
               <Image source={heartIcon}/>
           </Text>
        </View>
    );
}

export default Landing;
