import React, { useState } from 'react';
import { View , Image, Text, Linking} from 'react-native';

import { RectButton } from 'react-native-gesture-handler';
import AsyncStorege from '@react-native-community/async-storage';

import heartOutLineIcon from '../../assets/images/icons/heart-outline.png';
import unfavoriteIcon from '../../assets/images/icons/unfavorite.png';
import whatsappIcon from '../../assets/images/icons/whatsapp.png';

import styles from './styles';


export interface Teacher {
    id: number;
    avatar: string;
    bio: string;
    cost: number;
    name: string;
    subject: string;
    whatsapp: string;
}

interface TeacherItemProps {
    teacher: Teacher;
    favorited: boolean;
}

const TeacherItem: React.FC<TeacherItemProps> = ({ teacher, favorited }) => {

    const [isFavorited, setIsFavorited] = useState(favorited);

    function handleLinkToWhasapp(){
        Linking.openURL(`whatsapp://send?phone=${teacher.whatsapp}`)
    }

    async function handleToggleFavorite(){
        const favorites = await AsyncStorege.getItem('favorites');
            
        let favoritesArray = [];
        
        if (favorites) {
            favoritesArray = JSON.parse(favorites);
        }

        if (isFavorited) {

        }else{
            favoritesArray.push(teacher);
            
            setIsFavorited(true);

            await AsyncStorege.setItem('favorites', JSON.stringify(favoritesArray));
        }
    }

    return(
        <View style={styles.container}>
            <View style={styles.profile}>
                <Image
                    style={styles.avatar}
                    source={{ uri: teacher.avatar }} 
                />

                <View style={styles.profileInfo} >
                    <Text style={styles.name}>{teacher.name}</Text>
                    <Text style={styles.subject}>{teacher.subject}</Text>
                </View>
            </View>
            <Text style={styles.bio}>
                {teacher.bio}
            </Text>
            
            <View style={styles.footer}>
                <Text style={styles.price}>
                    Preço/Hora {'   '}
                    <Text style={styles.priceValue}>R$ {teacher.cost}</Text>
                </Text>

                <View style={styles.buttonsContainer}>

                    <RectButton 
                        onPress={handleToggleFavorite}
                        style={[
                                styles.favoriteButton, 
                                isFavorited ? styles.favorited : {},
                            ]}
                        
                        >
                        { isFavorited 
                            ? <Image  source={heartOutLineIcon} /> 
                            : <Image  source={unfavoriteIcon} />
                        }
                       
                        
                    </RectButton>

                    <RectButton onPress={handleLinkToWhasapp} style={styles.contactButton}>
                        <Image  source={whatsappIcon} />
                        <Text style={styles.contactButtonText}>Entrar em contato</Text>
                    </RectButton>

                </View>
            </View>
        </View>
    );
}

export default TeacherItem;
